mapper.select:
SELECT MEM_ID
FROM MEMBERS
WHERE MEM_ID = #{MEM_ID} AND MEM_PW = #{MEM_PW}

mapper.insert:
INSERT INTO MEMBERS ( MEM_ID
					, MEM_NICK
					, MEM_GENDER
					, MEM_EMAIL
					, MEM_TEL
					, MEM_NAME
					, MEM_BIRTH
					, MEM_SNS
					, MEM_STATUS
					, MEM_PW)
VALUES (  #{MEM_ID}
		, #{MEM_NICK}
		, #{MEM_GENDER}
		, #{MEM_EMAIL}
		, #{MEM_TEL}
		, #{MEM_NAME}
		, #{MEM_BIRTH}
		, #{MEM_SNS}
		, #{MEM_STATUS}
		, #{MEM_PW})

mapper.select:
SELECT MEM_PW
FROM MEMBERS
WHERE MEM_ID = #{MEM_ID}
AND MEM_EMAIL = #{MEM_EMAIL}
AND MEM_SNS = 'F'
AND MEM_STATUS = 'F'
