mapper.select:
SELECT P.MEM_NO , P.PRO_NO , P.PRO_VIEWS , P.PRO_DATE , P.PRO_CATEGORY , P.PRO_LOCATION , P.PRO_NEG , P.PRO_PRICE , P.PRO_STATE , P.PRO_STATUS , P.PRO_TITLE , P.PRO_TYPE , P.PRO_CONTENT , SUBSTRING_INDEX(P.PRO_IMG, ',', 1) AS PRO_IMG FROM PRODUCT P
RIGHT OUTER JOIN LIKED L ON P.PRO_NO = L.PRO_NO WHERE L.MEM_NO = #{MEM_NO} AND P.PRO_NO IS NOT NULL
WHERE P.PRO_STATUS = 'STD1' AND P.MEM_NO = #{MEM_NO}
WHERE P.PRO_STATUS = 'STD2' AND P.MEM_NO = #{MEM_NO}
JOIN PAYMENT PAY ON P.PRO_NO = PAY.PRO_NO WHERE PAY.SEL_MEM_NO = #{MEM_NO}
ORDER BY P.PRO_DATE
ORDER BY P.PRO_PRICE DESC
ORDER BY P.PRO_PRICE ASC
ORDER BY P.PRO_VIEWS

	SELECT CHA_NO
		, CHA_LOG
		, CHA_MEM1
		, CHA_MEM2 
	FROM CHATTING
	WHERE 1 = 1

	
		AND CHA_MEM1 = #{CHA_MEM1}
		AND CHA_MEM2 = #{CHA_MEM2}
	
	
		AND CHA_NO = #{CHA_NO}

mapper.update:
UPDATE CHATTING
SET CHA_LOG = JSON_ARRAY_APPEND(
	CHA_LOG,
	'$', 
	JSON_OBJECT(
		'USERID', #{USERID},
		'TIME', #{TIME},
		'TEXT', #{TEXT}
	)
)
WHERE CHA_NO = #{CHA_NO}
