mapper.select:
	SELECT CHA_NO
		, CHA_LOG
		, CHA_MEM1
		, CHA_MEM2 
	FROM CHATTING
	WHERE 1 = 1

	
		AND CHA_MEM1 = #{CHA_MEM1}
		AND CHA_MEM2 = #{CHA_MEM2}
	
	
		AND CHA_NO = #{CHA_NO}

mapper.update:
UPDATE CHATTING
SET CHA_LOG = JSON_ARRAY_APPEND(
	CHA_LOG,
	'$', 
	JSON_OBJECT(
		'USERID', #{USERID},
		'TIME', #{TIME},
		'TEXT', #{TEXT}
	)
)
WHERE CHA_NO = #{CHA_NO}
